    public class SpotifyWebApiTest
    {
        private readonly ILog _log = Substitute.For<ILog>();
        private readonly AppSettings _appSettings = Substitute.For<AppSettings>();
        private readonly SpotifyWebApi _spotifyWebApi;
        private readonly FakeResponseHandler _responseHandler = new FakeResponseHandler();
        private readonly SpotifyHttpClient _spotifyHttpClient;

        public SpotifyWebApiTest()
        {
            _spotifyWebApi = new SpotifyWebApi(_log, _appSettings);
            _spotifyHttpClient = new SpotifyHttpClient("https://api.spotify.com/v1/", _log, "", _appSettings, null,
                _responseHandler);
            _spotifyWebApi.GetType()
                .GetField("_client", BindingFlags.NonPublic | BindingFlags.Instance)
                .SetValue(_spotifyWebApi,
                    _spotifyHttpClient);
        }


        private string profileResponse =
            "{\r\n  \"birthdate\" : \"1990-12-13\",\r\n  \"country\" : \"BY\",\r\n  \"display_name\" : \"Alexy Petrenko\",\r\n  \"email\" : \"ap@example.com\",\r\n  \"external_urls\" : {\r\n    \"spotify\" : \"https://open.spotify.com/user/12394855431\"\r\n  },\r\n  \"followers\" : {\r\n    \"href\" : null,\r\n    \"total\" : 18\r\n  },\r\n  \"href\" : \"https://api.spotify.com/v1/users/10394850332\",\r\n  \"id\" : \"10394850332\",\r\n  \"images\" : [ {\r\n    \"height\" : null,\r\n    \"url\" : \"http://i.imgur.com/wcmb40Y.jpg?fb\",\r\n    \"width\" : null\r\n  } ],\r\n  \"product\" : \"premium\",\r\n  \"type\" : \"user\",\r\n  \"uri\" : \"spotify:user:12394855431\"\r\n}";

        [Fact]
        public async Task TestGetProfile()
        {
            _responseHandler.AddFakeResponse(new Uri("https://api.spotify.com/v1/me"), new HttpResponseMessage
            {
                Content = new StringContent(profileResponse)
            });

            var profileResult = await _spotifyWebApi.GetProfile();
            Assert.NotNull(profileResult);
            Assert.Equal("spotify:user:12394855431", profileResult.Uri);
            Assert.Equal("Alexy Petrenko", profileResult.DisplayName);
        }

